00711             static const ::std::string clean(const std::string& input) noexcept {
00712                 short unsigned int pos = 0; ::std::string result;
00713                 while (pos < input.size()) {
00714                     switch (input[pos]) {
00715                         case '+': result += '+'; break;
00716                         case '-': result += '-'; break;
00717                         case '*': result += '*'; break;
00718                         case '/': result += '/'; break;
00719                         default: if (Lexer::is_integer(input[pos])) {result += input[pos];} break;
00720                     } pos++;
00721                 } return result;
00722             }
00729             static constexpr inline bool is_integer(const char& c) noexcept {return (c >= '0' && c <= '9');}
00730         public:
00735             Lexer(const std::string& input) noexcept : _input(Lexer::clean(input)), _pos(0) {}
00740             const Token next() noexcept {
00741                 if (this->_pos >= this->_input.size()) {return {TokenType::T_EOF, {0}};}
00742                 switch (this->_input[this->_pos]) {
00743                     case '+': this->_pos++; return {TokenType::T_PLUS, {0}};
00744                     case '-': this->_pos++; return {TokenType::T_MINUS, {0}};
00745                     case '*': this->_pos++; return {TokenType::T_MULTIPLICATION, {0}};
00746                     case '/': this->_pos++; return {TokenType::T_DIVISION, {0}};
00747                     default: this->_pos++; return {TokenType::T_NUMBER, {(char) this->_input[this->_pos - 1]}};
00748                 }
00749             }
00753             ~Lexer() noexcept = default;
00754     };
00760     class Parser {
00761         private:
00765             Lexer _lexer;
00766         public:
00771             Parser(const std::string& input) noexcept : _lexer(input) {}
00776             const int parse_number() noexcept {
00777                 bool positive = true;
00778                 ::std::string buffer = "0";
00779                 while (true) {
00780                     const Token token = this->_lexer.next();
00781                     switch (token.type) {
00782                         case TokenType::T_MINUS: positive = !positive; break;
00783                         case TokenType::T_NUMBER: buffer += token.value.c; break;
00784                         case TokenType::T_EOF: return positive ? std::stoi(buffer) : -1 * std::stoi(buffer);
00785                     }
00786                 }
00787             }
00793             const emth::Polynomial parse_operation(const ::std::vector<emth::Polynomial>& ps) {
00794                 bool hook = false;
00795                 std::vector<emth::Polynomial> data; std::vector<TokenType> operations;
00796                 while (true) {
00797                     const Token token = this->_lexer.next();
00798                     if (token.type == TokenType::T_EOF) {break;}
00799                     switch (token.type) {
00800                         case TokenType::T_NUMBER: if (!hook && (unsigned int) std::stoi(std::string(1, token.value.c)) < ps.size()) {data.push_back(ps[std::stoi(std::string(1, token.value.c))]); hook = true;} break;
00801                         default: if (hook) {operations.push_back(token.type); hook = false;} break;
00802                     }
00803                 }
00804                 for (unsigned int i = 0; i < operations.size(); i++) {
00805                     if (operations[i] == TokenType::T_MULTIPLICATION || operations[i] == TokenType::T_DIVISION) {
00806                         switch (operations[i]) {
00807                             case TokenType::T_MULTIPLICATION: data[i] *= data[i + 1]; break;
00808                             case TokenType::T_DIVISION: data[i] /= data[i + 1]; break;
00809                         } data.erase(data.begin() + i + 1); operations.erase(operations.begin() + i); i--;
00810                     }
00811                 }
00812                 for (unsigned int i = 0; i < operations.size(); i++) {
00813                     switch (operations[i]) {
00814                         case TokenType::T_PLUS: data[i] += data[i + 1]; break;
00815                         case TokenType::T_MINUS: data[i] -= data[i + 1]; break;
00816                     } data.erase(data.begin() + i + 1); operations.erase(operations.begin() + i); i--;
00817                 } return data[0];
00818             }
00822             ~Parser() noexcept = default;
00823     };
