00873             const int save_json() const noexcept {
00874                 try {
00875                     rapidjson::StringBuffer buffer;
00876                     rapidjson::Writer<rapidjson::StringBuffer> writer(buffer);
00877                     this->json.Accept(writer);
00878                     this->storage.write(buffer.GetString());
00879                 } catch (::std::exception& e) {return 1;} return 0;
00880             }
00885             const int read_json() noexcept {
00886                 try {
00887                     if (this->json.IsArray()) {
00888                         for (const auto& o : this->json.GetArray()) {
00889                             if (o.IsString()) {this->polynomials.push_back(emthp::Parser(o.GetString()).parse_polynomial());}
00890                         }
00891                     }
00892                 } catch (::std::exception& e) {return 1;} return 0;
00893             }
00898             const int cycle_focus() noexcept {
00899                 #define swpa(o) this->active = o; this->terminal->swap_active(o);
00900                 switch(this->active) {
00901                     case LWIN: swpa(RWIN); return 1;
00902                     case RWIN: swpa(BWIN); break;
00903                     case BWIN: swpa(LWIN); return 1;
00904                 } return 0;
00905             }
00910             const int scroll_loop() noexcept {
00911                 this->terminal->set_ldata(V_SCROLL);
00912                 this->terminal->flush();
00913                 while(true) {
00914                     switch(this->terminal->get_short_input()) {
00915                         case KEY_ESC: return 0;
00916                         case KEY_TAB: return 1;
00917                         case KEY_UP: this->terminal->scroll_up(this->active); break;
00918                         case KEY_DOWN: this->terminal->scroll_up(this->active); break;
00919                     } this->terminal->flush();
00920                 } return 0;
00921             }
