00930                     this->add_polynomial(polynomial);
00931                 } catch (::std::exception& e) {return 1;} 
00932                 this->terminal->set_prompt("Option:"); return 0;
00933             }
00938             const int add_polynomial(const emth::Polynomial& polynomial) noexcept {
00939                 try {
00940                     this->json.PushBack(rapidjson::Value(polynomial.get_expression().c_str(), this->json.GetAllocator()).Move(), this->json.GetAllocator());
00941                     this->polynomials.push_back(::std::move(polynomial));
00942                     this->terminal->set_rdata(this->stringify_polynomials());
00943                     this->save_json();
00944                 } catch (::std::exception& e) {return 1;} 
00945                 return 0;
00946             }
00951             const int remove_polynomial(const unsigned int id) noexcept {
00952                 try {
00953                     if (id < this->polynomials.size()) {
00954                         this->polynomials.erase(this->polynomials.begin() + id);
00955                         this->json.Erase(this->json.Begin() + id);
00956                         this->terminal->set_rdata(this->stringify_polynomials());
00957                         this->save_json();
00958                     } else {this->terminal->echo_result("No polynomial with index:" + std::to_string(id));}
00959                 } catch (::std::exception& e) {return 1;} return 0;
00960             }
00965             const ::std::vector<::std::string> stringify_polynomials() const noexcept {::std::vector<::std::string> buffer; for (unsigned int i = 0; i < this->polynomials.size(); i++) {buffer.push_back("[" + std::to_string(i) + "] " + ::std::move(this->polynomials[i].get_expression()));} return buffer;}
00970             const int main_loop() {
00971                 this->terminal->set_ldata(V_MENU);
00972                 this->terminal->flush();
00973                 while(true) {
00974                     switch(this->terminal->get_short_input()) {
00975                         case KEY_ESC: return 0; case KEY_SPC: return 1; 
00976                         case KEY_TAB: return 2;
00977                         case KEY_E: this->get_polynomial(); break;
00978                         case KEY_Q: this->terminal->set_prompt("Id:"); this->terminal->flush(); this->remove_polynomial(Application::to_int(this->terminal->get_long_input())); this->terminal->set_prompt("Option:"); break;
00979                     } this->terminal->flush();
00980                 } return 0;
00981             }
00986             const int operation_loop() {
00987                 if (!this->polynomials.empty()) {
00988                     this->terminal->set_ldata(V_OPERATION);
00989                     this->terminal->flush();
00990                     while(true) {
00991                         switch(this->terminal->get_short_input()) {
00992                             case KEY_ESC: return 0; case KEY_TAB: return 2;
00993                             case KEY_SPC: this->calculate(0); return 1;
00994                             case KEY_1: this->calculate(1); return 1;
00995                             case KEY_2: this->calculate(2); return 1;
00996                             case KEY_3: this->calculate(3); return 1;
00997                             case KEY_4: this->calculate(4); return 1;
00998                             case KEY_5: this->calculate(5); return 1;
00999                         } this->terminal->flush();
01000                     }
01001                 } this->terminal->echo_result("No polynomials registered!"); return 0;
01002             }
01007             void calculate(const int t) noexcept {
01008                 this->terminal->set_prompt("Input:"); this->terminal->flush();
01009                 if (t == 0) {
01010                     try {
01011                         std::string in = this->terminal->get_long_input();
01012                         const emth::Polynomial r = Parser(in).parse_operation(this->polynomials);
01013                         this->terminal->echo_result(r.is_empty() ? "0" : r.get_expression());
01014                         if (in[0] == '_') {this->add_polynomial(r);}
01015                     } catch (int e) {this->terminal->echo_result("No polynomial with index:" + std::to_string(e));}
01016                 } else {
01017                     const unsigned int sinput = Application::to_int(this->terminal->get_long_input());
01018                     if (sinput < this->polynomials.size()) {
01019                         switch(t) {
01020                             case 1:
01021                                 this->terminal->set_prompt("Value:"); this->terminal->flush();
01022                                 this->terminal->echo_result((::std::stringstream() << this->polynomials[sinput].get_value(Application::to_int(this->terminal->get_long_input()))).str());
01023                                 break;
01024                             case 2: this->terminal->echo_result(this->polynomials[sinput].get_derivative().get_expression()); break;
01025                             case 3: this->terminal->echo_result(this->polynomials[sinput].get_integral().get_expression()); break;
01026                             case 4: this->terminal->echo_result(vtoss(this->polynomials[sinput].get_roots())); break;
01027                             case 5: launch_plane(this->polynomials[sinput]); break;
01028                         }
01029                     } else {this->terminal->echo_result("No polynomial with index:" + std::to_string(sinput));}
01030                 } this->terminal->set_prompt("Option:"); return;
01031             }
01036             void launch_plane(const emth::Polynomial& function) const noexcept {
01037                 this->terminal->set_prompt("X Offset:"); this->terminal->flush();
01038                 int x = Application::to_int(this->terminal->get_long_input());
01039                 this->terminal->set_prompt("Y Offset:"); this->terminal->flush();
01040                 int y = Application::to_int(this->terminal->get_long_input());
01041                 std::thread plane_thread([function, x, y](){Plane p(-x, -y, function); p.run(); return;});
01042                 plane_thread.detach(); return;
01043             }
01049             static const int to_int(const ::std::string& number) noexcept {return Parser(number).parse_number();}
01055             static const std::string vtoss(const std::vector<std::complex<double>>& v) noexcept {std::stringstream ss; ss << "["; ctoss(ss, v[0]); for(unsigned int i = 1; i < v.size(); i++) {ss << ", "; ctoss(ss, v[i]);} ss << "]"; return ss.str();}
01061             static const void ctoss(std::stringstream& ss, const std::complex<double>& c) noexcept {ss << "("; if (std::abs(c.real()) > 1e-9 && std::abs(c.imag()) > 1e-9) {ss << c.real() << " " << ((c.imag() > 0) ? "+ " : "- ") << std::abs(c.imag()) << "i";} else if (std::abs(c.real()) > 1e-9) {ss << c.real();} else if (std::abs(c.imag()) > 1e-9) {ss << c.imag() << "i";} else {ss << "0";} ss << ")"; return;}
01062         public:
01067             static Application* init() noexcept {
01068                 if (Application::instance != nullptr) {delete Application::instance;}
01069                 return Application::instance = new Application();
01070             }
01074             void run() {
01075                 bool hook = false;
01076                 while(true) {
01077                     switch(this->main_loop()) {
01078                         case 0: return;
01079                         case 1: if (this->operation_loop() == 2 && this->cycle_focus() == 1) {hook = true;} break;
01080                         case 2: if (this->cycle_focus() == 1) {hook = true;} break;
01081                     }
01082                     while (hook) {
01083                         switch (this->scroll_loop()) {
01084                             case 0: return;
01085                             case 1: if (this->cycle_focus() != 1) {hook = false;}; break;
01086                         }
01087                     }
01088                 } return;
01089             }
01093             ~Application() noexcept {
01094                 Application::instance = nullptr;
01095                 delete this->terminal;
01096                 this->save_json(); return;
01097             }
01098     }; Application* Application::instance = nullptr;
01099 }
